mapper.select:
select *
from (select listing_number from real_estate_sale order by LISTING_NUMBER desc)
where rownum = 1

select listing_number as listingNumber, to_char(sold_date, 'YYYY.MM.DD') as soldDate, amount, start_date as startDate, expiration_date as expirationDate, dividend
from sold_building
where listing_number = #{LN}

mapper.update:
UPDATE real_estate_sale
evaluation = #{evaluation}, state = #{state}, price = #{price}, reasonable_price = #{reasonablePrice}, introduction = #{introduction},
WHERE listing_number = #{listingNumber}
